'use strict'

const base = require('mssql/lib/base')
const ConnectionPool = require('mssql/lib/tedious/connection-pool')
const Transaction = require('mssql/lib/tedious/transaction')
const Request = require('mssql/lib/tedious/request')

module.exports = Object.assign({
  ConnectionPool,
  Transaction,
  Request,
  PreparedStatement: base.PreparedStatement
}, base.exports)

Object.defineProperty(module.exports, 'Promise', {
  enumerable: true,
  get: () => {
    return base.Promise
  },
  set: (value) => {
    base.Promise = value
  }
})

Object.defineProperty(module.exports, 'valueHandler', {
  enumerable: true,
  value: base.valueHandler,
  writable: false,
  configurable: false
})

base.driver.name = 'tedious'
base.driver.ConnectionPool = ConnectionPool
base.driver.Transaction = Transaction
base.driver.Request = Request
